// src/App.tsx
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import * as signalR from '@microsoft/signalr';
import './App.css';

interface Item {
  id: string;
  name: string;
  price: number;
  updatedAt: string;
}

const App: React.FC = () => {
  const [items, setItems] = useState<Item[]>([]);
  const [isSubscribed, setIsSubscribed] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const connectionRef = useRef<signalR.HubConnection | null>(null);
  const previousPricesRef = useRef<Record<string, number>>({});

  useEffect(() => {
    // Initialize previous prices
    const initialPreviousPrices: Record<string, number> = {};
    items.forEach(item => {
      initialPreviousPrices[item.id] = item.price;
    });
    previousPricesRef.current = initialPreviousPrices;
  }, [items]);

  useEffect(() => {
    // Fetch initial items
    const fetchItems = async () => {
      try {
        const response = await axios.get<Item[]>('https://localhost:7078/items');
        setItems(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch items');
        setLoading(false);
      }
    };

    fetchItems();

    // Initialize SignalR connection
    connectionRef.current = new signalR.HubConnectionBuilder()
      .withUrl('https://localhost:7078/priceHub')
      .configureLogging(signalR.LogLevel.Debug)
      .build();

      // Add these event handlers
    connectionRef.current.onclose(error => {
      console.error("Connection closed:", error);
    });

    connectionRef.current.onreconnecting(error => {
      console.log("Reconnecting:", error);
    });

    connectionRef.current.onreconnected(connectionId => {
      console.log("Reconnected with ID:", connectionId);
    });

    return () => {
      if (connectionRef.current) {
        connectionRef.current.stop();
      }
    };
  }, []);

  const handleSubscribe = async () => {
    if (!connectionRef.current) return;

    try {
      await connectionRef.current.start();
      await connectionRef.current.invoke('SubscribeToPriceUpdates');

      connectionRef.current.on('ReceivePriceUpdates', (updatedItems: Item[]) => {
        // Update previous prices before setting new items
        const newPreviousPrices: Record<string, number> = {};
        items.forEach(item => {
          newPreviousPrices[item.id] = item.price;
        });
        previousPricesRef.current = newPreviousPrices;

        setItems(prevItems => {
          return updatedItems.map(updatedItem => {
            const existingItem = prevItems.find(item => item.id === updatedItem.id);
            return existingItem ? updatedItem : existingItem!;
          });
        });
      });

      setIsSubscribed(true);
    } catch (err) {
      console.error('Error subscribing to price updates:', err);
    }
  };

  const handleUnsubscribe = async () => {
    if (!connectionRef.current) return;

    try {
      await connectionRef.current.invoke('UnsubscribeFromPriceUpdates');
      setIsSubscribed(false);
    } catch (err) {
      console.error('Error unsubscribing from price updates:', err);
    }
  };

  const getPriceChangeIndicator = (item: Item) => {
    const previousPrice = previousPricesRef.current[item.id];
    if (previousPrice === undefined || previousPrice === item.price) {
      return '→'; // No change
    }
    return previousPrice < item.price ? '↑' : '↓';
  };

  const getPriceChangeColor = (item: Item) => {
    const previousPrice = previousPricesRef.current[item.id];
    if (previousPrice === undefined || previousPrice === item.price) {
      return 'black'; // No change
    }
    return previousPrice < item.price ? 'green' : 'red';
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="app">
      <h1>Price Updates</h1>
      <div className="buttons">
        <button
          onClick={handleSubscribe}
          disabled={isSubscribed}
          className="subscribe-btn"
        >
          Subscribe to Price Updates
        </button>
        <button
          onClick={handleUnsubscribe}
          disabled={!isSubscribed}
          className="unsubscribe-btn"
        >
          Unsubscribe from Price Updates
        </button>
      </div>
      <div className="items-list">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>Updated At</th>
              <th>Change</th>
            </tr>
          </thead>
          <tbody>
            {items.map(item => (
              <tr key={item.id}>
                <td>{item.id.substring(0, 8)}...</td>
                <td>{item.name}</td>
                <td>${item.price.toFixed(2)}</td>
                <td>{new Date(item.updatedAt).toLocaleTimeString()}</td>
                <td style={{ color: getPriceChangeColor(item) }}>
                  {getPriceChangeIndicator(item)}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
