import React, { useEffect, useState } from 'react';
import * as signalR from '@microsoft/signalr';
import './App.css';
import Item from './models/Items';
import PriceChange from './models/PriceChange';

const App = () => {
  const [items, setItems] = useState<Item[]>([]);
  const [connection, setConnection] = useState<signalR.HubConnection | null>(null);
  const [status, setStatus] = useState('Disconnected');
  const [subscribed, setSubscribed] = useState(false);
  const [loading, setLoading] = useState(true);
  const [priceStatus, setPriceStatus] = useState<Record<string, PriceChange>>({});

  // ðŸ”„ Load items initially
  useEffect(() => {
    const fetchItems = async () => {
      try {
        const res = await fetch('https://localhost:7078/api/items');
        const data = await res.json();
        setItems(data);

        // Initialize all prices as "same"
        const statusMap: Record<string, PriceChange> = {};
        data.forEach((item: Item) => {
          statusMap[item.id] = 'same';
        });
        setPriceStatus(statusMap);

      } catch (err) {
        console.error('Failed to fetch items:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchItems();
  }, []);

  // ðŸ”Œ Setup SignalR connection
  useEffect(() => {
    const newConnection = new signalR.HubConnectionBuilder()
      .withUrl('https://localhost:7078/api/pricehub', {
        transport: signalR.HttpTransportType.WebSockets,
        withCredentials: true,
      })
      .configureLogging(signalR.LogLevel.Information)
      .build();

    setConnection(newConnection);

    return () => {
      newConnection.stop();
    };
  }, []);

  // ðŸ“¶ Connect to the hub
  useEffect(() => {
    if (!connection) return;

    const start = async () => {
      try {
        setStatus('Connecting...');
        await connection.start();
        setStatus('Connected');
      } catch (err) {
        setStatus('Connection Failed');
        console.error('Connection failed:', err);
        setTimeout(start, 5000);
      }
    };

    start();

    return () => {
      connection.off('ReceivePriceUpdates');
      connection.off('ReceivePriceUpdate');
    };
  }, [connection]);

  // ðŸ” Update price status helper
  const updatePrices = (incomingItems: Item[]) => {
    setItems(current => {
      const updatedStatus: Record<string, PriceChange> = {};

      incomingItems.forEach(newItem => {
        const existing = current.find(x => x.id === newItem.id);
        if (!existing) {
          updatedStatus[newItem.id] = 'same';
        } else if (newItem.price > existing.price) {
          updatedStatus[newItem.id] = 'up';
        } else if (newItem.price < existing.price) {
          updatedStatus[newItem.id] = 'down';
        } else {
          updatedStatus[newItem.id] = 'same';
        }
      });

      setPriceStatus(updatedStatus);
      return incomingItems;
    });
  };

  // â–¶ï¸ Subscribe to live updates
  const subscribe = async () => {
    if (!connection) return;

    try {
      await connection.invoke('SubscribeToPriceUpdates');
      setSubscribed(true);

      connection.on('ReceivePriceUpdates', (updatedItems: Item[]) => {
        updatePrices(updatedItems);
      });

      connection.on('ReceivePriceUpdate', (updatedItem: Item) => {
        updatePrices(items.map(i => (i.id === updatedItem.id ? updatedItem : i)));
      });
    } catch (err) {
      console.error('Subscription failed:', err);
    }
  };

  // â¹ Unsubscribe from updates
  const unsubscribe = async () => {
    if (!connection) return;

    try {
      await connection.invoke('UnsubscribeFromPriceUpdates');
      setSubscribed(false);
      connection.off('ReceivePriceUpdates');
      connection.off('ReceivePriceUpdate');
    } catch (err) {
      console.error('Unsubscribe failed:', err);
    }
  };

  // ðŸ–¼ UI rendering
  if (loading) return <div className="loading">Loading items...</div>;

  return (
    <div className="app">
      <h1>Live Price Updates</h1>

      <div className="connection-info">
        <span className={`connection-dot ${status.toLowerCase()}`}></span>
        <span>Connection: {status} | Updates: {subscribed ? 'ON' : 'OFF'}</span>
      </div>

      <div className="controls">
        <button onClick={subscribe} disabled={!connection || subscribed}>Start Updates</button>
        <button onClick={unsubscribe} disabled={!connection || !subscribed}>Stop Updates</button>
      </div>

      <table className="prices-table">
        <thead>
          <tr>
            <th>Product</th>
            <th>Price</th>
            <th>Last Updated</th>
            <th>Change</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>${item.price.toFixed(2)}</td>
              <td>{new Date(item.updatedAt).toLocaleTimeString()}</td>
              <td className={`change-arrow ${priceStatus[item.id]}`}>
                {priceStatus[item.id] === 'up' ? 'â†‘' : 
                 priceStatus[item.id] === 'down' ? 'â†“' : 'â†’'}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
